      (seen_list := BeamSet.add (new_rownum, new_colnum, beam.direction) !seen_list;
       let beam = { beam with traversed_set = PointSet.add new_point beam.traversed_set } in
       match (beam.direction, grid.(new_rownum).(new_colnum)) with
       | (Left, '.') -> traverse_beam_helper beam grid (new_rownum, new_colnum - 1)
       | (Left, '-') -> traverse_beam_helper beam grid (new_rownum, new_colnum - 1)
       | (Right, '.') -> traverse_beam_helper beam grid (new_rownum, new_colnum + 1)
       | (Right, '-') -> traverse_beam_helper beam grid (new_rownum, new_colnum + 1)
       | (Up, '.') -> traverse_beam_helper beam grid (new_rownum - 1, new_colnum)
       | (Up, '|') -> traverse_beam_helper beam grid (new_rownum - 1, new_colnum)
       | (Down, '.') -> traverse_beam_helper beam grid (new_rownum + 1, new_colnum)
       | (Down, '|') -> traverse_beam_helper beam grid (new_rownum + 1, new_colnum)
       | (Left, '\\') -> traverse_beam_helper { beam with direction = Up } grid (new_rownum - 1, new_colnum)
       | (Right, '\\') -> traverse_beam_helper { beam with direction = Down } grid (new_rownum + 1, new_colnum)
       | (Up, '\\') -> traverse_beam_helper { beam with direction = Left } grid (new_rownum, new_colnum - 1)
       | (Down, '\\') -> traverse_beam_helper { beam with direction = Right } grid (new_rownum, new_colnum + 1)
       | (Left, '/') -> traverse_beam_helper { beam with direction = Down } grid (new_rownum + 1, new_colnum)
       | (Right, '/') -> traverse_beam_helper { beam with direction = Up } grid (new_rownum - 1, new_colnum)
       | (Up, '/') -> traverse_beam_helper { beam with direction = Right } grid (new_rownum, new_colnum + 1)
       | (Down, '/') -> traverse_beam_helper { beam with direction = Left } grid (new_rownum, new_colnum - 1)
       | (Up, '-') -> PointSet.union (traverse_beam_helper { beam with direction = Left } grid (new_rownum, new_colnum - 1)) (traverse_beam_helper { beam with direction = Right } grid (new_rownum, new_colnum + 1))
       | (Down, '-') -> PointSet.union (traverse_beam_helper { beam with direction = Left } grid (new_rownum, new_colnum - 1)) (traverse_beam_helper { beam with direction = Right } grid (new_rownum, new_colnum + 1))
       | (Left, '|') -> PointSet.union (traverse_beam_helper { beam with direction = Up } grid (new_rownum - 1, new_colnum)) (traverse_beam_helper { beam with direction = Down } grid (new_rownum + 1, new_colnum))
       | (Right, '|') -> PointSet.union (traverse_beam_helper { beam with direction = Up } grid (new_rownum - 1, new_colnum)) (traverse_beam_helper { beam with direction = Down } grid (new_rownum + 1, new_colnum))
       | _ -> failwith "wrong case!") in
  traverse_beam_helper beam grid initial_point

let get_energized_number b g i =
    traverse_beam b g i |> PointSet.cardinal

let make_num_list n =
  let rec make_num_list_helper n l =
    if n < 1 then l else make_num_list_helper (n - 1) ((n - 1)::l)
  in
  make_num_list_helper n []

let () =
  get_energized_number { direction = Right ; traversed_set = PointSet.empty } grid (0, 0) |> string_of_int |> print_endline;
  let n_cols = (Array.length grid.(0)) in
  let n_rows = (Array.length grid) in
  let top_list =
    make_num_list n_cols
    |> List.map (fun x -> get_energized_number { direction = Down ; traversed_set = PointSet.empty } grid (0, x)) in
  let bottom_list =
    make_num_list n_cols
    |> List.map (fun x -> get_energized_number { direction = Up ; traversed_set = PointSet.empty } grid (n_rows - 1, x)) in
  let left_list =
    make_num_list n_rows
    |> List.map (fun x -> get_energized_number { direction = Right ; traversed_set = PointSet.empty } grid (x, 0)) in
  let right_list =
    make_num_list n_rows
    |> List.map (fun x -> get_energized_number { direction = Right ; traversed_set = PointSet.empty } grid (x, n_cols - 1)) in
  [top_list;bottom_list;left_list;right_list]
  |> List.concat
  |> List.fold_left (fun x y -> max x y) 0
  |> string_of_int
  |> print_endline
